document.addEventListener("DOMContentLoaded",(()=>{var e=document.querySelectorAll(".question__top img"),s=document.querySelectorAll(".question__bottom");console.log(e);for(let t=0;t<e.length;t++)e[t].addEventListener("click",(()=>{s[t].classList.contains("opened")?(e[t].src="assets/images/icon-plus.svg",s[t].classList.toggle("opened")):(e[t].src="assets/images/icon-minus.svg",s[t].classList.toggle("opened"))}))}));