let initSlider=()=>{const e=document.querySelector(".illustration .illustration__carousel"),l=document.querySelectorAll(".carousel-control"),t=e.scrollWidth;l.forEach((l=>{l.addEventListener("click",(()=>{const t="prev-btn"==l.id?-1:1,o=e.clientWidth*t;e.scrollBy({left:o,behavior:"smooth"})}))})),e.addEventListener("scroll",(()=>{l[0].style.display=e.scrollLeft<=0?"none":"block",l[1].style.display=e.scrollLeft>=t?"none":"block"}))};window.addEventListener("DOMContentLoaded",initSlider);